import React, { useState } from 'react';
import Header from './Header.js';
import Confetti from './Confetti';
import Uploader from './Uploader';
import List from './ListCard.js';
import './App.css';

function App() {
  const [videoSrc, setVideoSrc] = useState(null);
  const [videoUploaded, setVideoUploaded] = useState(false);
  const [analysisResult, setAnalysisResult] = useState(null); // New state for analysis result

  const handleUploadComplete = (videoURL) => {
    setVideoSrc(videoURL);
    setVideoUploaded(true);
  };

  const handleAnalysisComplete = (result) => {
    setAnalysisResult(result);
  };

  return (
    <div>
      <Header />
      <Confetti />
      <div className="background">
        <Uploader 
          onUploadComplete={handleUploadComplete} 
          videoUploaded={videoUploaded} 
          videoSrc={videoSrc}
          onAnalysisComplete={handleAnalysisComplete}
        />
      *{analysisResult && <List result={analysisResult} />} {/* Pass the analysis result */}
      </div>
    </div>
  );
}

export default App;
