import React, { useState } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
//import { TikTokLogo } from './TikTokLogo.js'; // Assume you have a TikTok logo component

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    background-color: #000;
    color: #fff;
    font-family: 'Arial', sans-serif;
  }
`;

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #000;
`;

const Header = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  padding: 10px 20px;
  background-color: #fe2c55;
`;

const Title = styled.h1`
  font-size: 24px;
`;

const VideoUploadContainer = styled.div`
  display: ${({ show }) => (show ? 'none' : 'flex')};
  flex-direction: column;
  align-items: center;
  justify-content: center;
  border: 2px dashed #fff;
  padding: 50px;
  cursor: pointer;
`;

const VideoContainer = styled.div`
  display: ${({ show }) => (show ? 'block' : 'none')};
  width: 100%;
  max-width: 600px;
  margin: 20px 0;
`;

const ListsContainer = styled.div`
  display: ${({ show }) => (show ? 'flex' : 'none')};
  justify-content: space-between;
  width: 100%;
  max-width: 1200px;
  margin-top: 20px;
`;

const List = styled.div`
  flex: 1;
  margin: 0 10px;
  padding: 20px;
  background-color: #1a1a1a;
  border-radius: 10px;
`;

const App = () => {
  const [videoUploaded, setVideoUploaded] = useState(false);

  const handleVideoUpload = () => {
    setVideoUploaded(true);
  };

  return (
    <>
      <GlobalStyle />
      <AppContainer>
        <Header>
          <Title>TikTok Music Recommender</Title>
        </Header>
        <VideoUploadContainer show={videoUploaded} onClick={handleVideoUpload}>
          Drag video here
        </VideoUploadContainer>
        <VideoContainer show={videoUploaded}>
          {/* Embed video here */}
          <video controls>
            <source src="path-to-video.mp4" type="video/mp4" />
          </video>
        </VideoContainer>
        <ListsContainer show={videoUploaded}>
          <List>
            <h2>Trending Songs on TikTok</h2>
            {/* List items */}
          </List>
          <List>
            <h2>New Discovery Songs</h2>
            {/* List items */}
          </List>
          <List>
            <h2>Recommended Music</h2>
            {/* List items */}
          </List>
        </ListsContainer>
      </AppContainer>
    </>
  );
};

export default App;
