import React, { useState } from 'react';
import Header from './Header.js';
import Confetti from './Confetti';
import './App.css';

function App() {
  const [dragging, setDragging] = useState(false);
  const [loading, setLoading] = useState(false);
  const [videoSrc, setVideoSrc] = useState(null);

  const handleDragEnter = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragging(false);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragging(false);
    setLoading(true);

    const files = e.dataTransfer.files;
    const videoFile = files[0];
    const videoURL = URL.createObjectURL(videoFile);

    setTimeout(() => {
      setVideoSrc(videoURL);
      setLoading(false);
    }, 2000); // Simulating loading delay

    // You can add further logic here to send the videoFile to backend for processing
  };

  return (
    <div>
      <Header />
      <Confetti />
      <div className="background">
        {/* PNG Button */}
        <div
          className={`drop-zone ${dragging ? 'dragging' : ''}`}
          onDragEnter={handleDragEnter}
          onDragLeave={handleDragLeave}
          onDragOver={handleDragOver}
          onDrop={handleDrop}
        >
          <img src="./img/vid-button.png" alt="Upload Button" style={{ height: '100px', width: '100px' }} />
          {loading ? (
            <p>Loading...</p>
          ) : videoSrc ? (
            <video controls width="280" height="400" src={videoSrc}></video>
          ) : (
            <p>Drag video here</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
